class Solution:
    def balancedStringSplit(self, s: str) -> int:
        seen = []
        oppos = {"L": "R", "R": "L"}
        counter = 0

        for direct in s:
            if seen and oppos[direct] == seen[-1]:
                seen.pop()
            else:
                seen.append(direct)
            if not seen:
                counter += 1

        return counter

