class Solution:
    def longestConsecutive(self, nums: list[int]) -> int:
        nums_set = set(nums)
        longest_concec = 0

        for num in nums_set:
            if not num - 1 in nums_set:
                curr_len = 1
                
                while num + curr_len in nums_set:
                    curr_len += 1
                longest_concec = max(longest_concec, curr_len)

        return longest_concec

