class Solution:
    def countPairs(self, nums: list[int], target: int) -> int:
        counter = 0
        nums.sort()
        l = 0
        r = len(nums) -1

        while l < r:
            if nums[l] + nums[r] < target:
                # [-1, 1, 1, 2, 3] (r - l) because left = -1, rigth = 2, so add every combination (1, 1, 2) before incresing the left pointer
                counter += r - l
                l += 1
            else:
                r -= 1

        return counter


# O(n2), O(1)
class Solution:
    def countPairs(self, nums, target):
        nums.sort()
        counter = 0

        for ind, num in enumerate(nums[:-1]):
            l = ind + 1

            while l < len(nums) and num + nums[l] < target:
                counter += 1
                l += 1

        return counter


