class Solution:
    def countPairs(self, nums: list[int], target: int) -> int:
        counter = 0

        for i_ind, i in enumerate(nums):
            for j_ind, j in enumerate(nums):
                if i_ind < j_ind and i + j < target:
                    counter += 1

        return counter

class Solution:
    def reversePrefix(self, word: str, ch: str) -> str:
        prefix_word = ""

        for letter in word:
            prefix_word = letter + prefix_word

            if letter == ch:
                return prefix_word + word[len(prefix_word):]

        return word


