class Solution:
    def rob(self, numbers: list[int]) -> int:
        """
        O(n), O(1)
        dp, bottom-up
        """
        if len(numbers) < 3:
            return max(numbers)

        cache = [numbers[0], max(numbers[:2])]

        for index in range(2, len(numbers)):
            cache = [cache[1], max(numbers[index] + cache[0], cache[1])]

        return cache[-1]


class Solution:
    def rob(self, numbers: list[int]) -> int:
        """
        O(n), O(1)
        dp, bottom-up
        mutate input list
        """
        if len(numbers) < 3:
            return max(numbers)

        numbers[1] = max(numbers[:2])

        for index in range(2, len(numbers)):
            numbers[index] = max(numbers[index] + numbers[index - 2], numbers[index - 1])

        return numbers[-1]


class Solution:
    def rob(self, numbers: list[int]) -> int:
        """
        O(n), O(n)
        dp, bottom-up
        """
        if len(numbers) < 3:
            return max(numbers)

        cache = [0] * len(numbers)
        cache[0] = numbers[0]
        cache[1] = max(numbers[:2])

        for index in range(2, len(numbers)):
            cache[index] = max(numbers[index] + cache[index - 2], cache[index - 1])

        return cache[-1]


class Solution:
    def rob(self, numbers: list[int]) -> int:
        """
        O(n), O(n)
        dp, top-down with memoization as hash map
        """
        memo = {}

        def dfs(index):
            if index >= len(numbers):
                return 0
            elif index in memo:
                return memo[index]

            memo[index] = max(numbers[index] + dfs(index + 2), dfs(index + 1))
            return memo[index]

        return dfs(0)


class Solution:
    def rob(self, numbers: list[int]) -> int:
        """
        O(n), O(n)
        dp, top-down with memoization as list
        """
        memo = [None] * (len(numbers) + 1)

        def dfs(index):
            if index >= len(numbers):
                return 0
            elif memo[index] != None:
                return memo[index]

            memo[index] = max(numbers[index] + dfs(index + 2), dfs(index + 1))
            return memo[index]

        return dfs(0)


class Solution:
    def rob(self, numbers: list[int]) -> int:
        """
        O(2^n), O(n)
        brute force, tle
        """
        def dfs(index):
            if index >= len(numbers):
                return 0

            return max(numbers[index] + dfs(index + 2), dfs(index + 1))

        return dfs(0)

class Solution:
    def rob(self, numbers: list[int]) -> int:
        """
        O(n), O(1)
        dp, bottom-up
        """
        if len(numbers) < 3:
            return max(numbers)
        
        def rob2(numbers):
            if len(numbers) < 3:
                return max(numbers)
            
            cache = [numbers[0], max(numbers[:2])]

            for index in range(2, len(numbers)):
                cache = [cache[1], max(numbers[index] + cache[0], cache[1])]

            return cache[1]

        return max(rob2(numbers[1:]), 
                   rob2(numbers[:-1]))


