class Solution:
    # validating sub-box
    def is_subbox_valid(self, row, col):
        seen = set()

        for r in range(3):
            for c in range(3):
                digit = self.board[r + row][c + col]

                if digit != ".":
                    if digit in seen:
                        return False
                    else:
                        seen.add(digit)
        return True

    def isValidSudoku(self, board):
        self.board = board

        rows = len(board)
        cols = len(board[0])

        # validating rows
        for row in board:
            seen = set()

            for digit in row:
                if digit != ".":
                    if digit in seen:
                        return False
                    else:
                        seen.add(digit)

        # validating columns
        for col in range(cols):
            seen = set()

            for row in range(rows):
                digit = board[row][col]

                if digit != ".":
                    if digit in seen:
                        return False
                    else:
                        seen.add(digit)

        # validating sub-boxes
        for row in range(0, 3*3, 3):
            for col in range(0, 3*3, 3):
                if not self.is_subbox_valid(row, col):
                    return False

        return True


