from collections import defaultdict
class Solution:
    def isValidSudoku(self, board: list[list[str]]) -> bool:
        row_uniq = defaultdict(set)
        col_uniq = defaultdict(set)
        box_uniq = defaultdict(set)

        for row in range(len(board)):
            for col in range(len(board[0])):
                element = board[row][col]

                if element != ".":
                    if (element in row_uniq[row]
                        or element in col_uniq[col]
                        or element in box_uniq[(row//3, col//3)]
                    ):
                        return False
                    else:
                        row_uniq[row].add(element)
                        col_uniq[col].add(element)
                        box_uniq[((row//3, col//3))].add(element)

        return True

class Solution:
    """
    @param: strs: a list of strings
    @return: encodes a list of strings to a single string.
    """

    def encode(self, words):
        encoded = ""
        
        for word in words:
            encoded += "Τ"
            encoded += str(len(word))
            encoded += word

        return encoded

    """
    @param: str: A string
    @return: decodes a single string to a list of strings
    """

    def decode(self, word):
        decoded = []
        
        while word:
            if word[0] == "Τ":
                word = word[1:]
                length = ""
                
                while word and word[0].isdigit():
                    length += word[0]
                    word = word[1:]

                length = int(length)
                decoded.append(word[:length])
                word = word[length:]
        
        return decoded

class Solution:
    def evalRPN(self, tokens: list[str]) -> int:
        stack = []

        for elem in tokens:
            if elem == "+":
                b = stack.pop()
                a = stack.pop()
                stack.append(a + b)
            elif elem == "-":
                b = stack.pop()
                a = stack.pop()
                stack.append(a - b)
            elif elem == "*":
                b = stack.pop()
                a = stack.pop()
                stack.append(a * b)
            elif elem == "/":
                b = stack.pop()
                a = stack.pop()
                stack.append(int(a / b))
            else:
                stack.append(int(elem))
        return stack[0]

