function dailyTemperatures(temps) {
  const daysDelta = Array(temps.length).fill(0)

  for (let indL = 0; indL < temps.length - 1; indL++) {
      for (let indR = indL + 1; indR < temps.length; indR++) {
          if (temps[indL] < temps[indR]) {
