 * @param {number} target
 * @return {number[][]}
 */
var combinationSum = function (candidates, target) {
  // sort candidates to ensure that candidate with index + 1 is greater than previous to not reapat solution
  candidates.sort();
  const combination = [];  // current combination
  const combinationList = [];  // solution

  function dfs(value, startIndex) {
    if (value == target) {  // target sum reached
      combinationList.push(combination.slice());  // push subset to subset_list
      return
    } else if (value > target) {  // if value is too large
      return
    }

    // check only equal or higher candidates
    for (let index = startIndex; index < candidates.length; index++) {
      let candidate = candidates[index];
      combination.push(candidate);
      dfs(value + candidate, index);
      combination.pop();
    }
  }

  dfs(0, 0);

  return combinationList
}

/**
 * @param {number[]} candidates
 * @param {number} target
 * @return {number[][]}
 */
var combinationSum2 = function (candidates, target) {
  candidates.sort();
  const combination = [];
  const combinationList = [];


  function dfs(index, value) {
    if (value == target) {
      combinationList.push(combination.slice());
      return
    } else if (index == candidates.length || value > target) {
      return
    }

    combination.push(candidates[index]);
    dfs(index + 1, value + candidates[index]);
    combination.pop();

    while (index + 1 < candidates.length && candidates[index + 1] == candidates[index])
      index++

    dfs(index + 1, value);
  }

  dfs(0, 0);

  return combinationList
}

/**
 * O(n!), O(n)
 * backtracking
 * @param {number[]} numbers
 * @return {number[][]}
 */
var permute = function (numbers) {
  const permutationList = [];

  const dfs = (left) => {
    if (left === numbers.length) {
      permutationList.push(numbers.slice());
      return
    }

    for (let right = left; right < numbers.length; right++) {
      [numbers[left], numbers[right]] = [numbers[right], numbers[left]];
      dfs(left + 1);
      [numbers[left], numbers[right]] = [numbers[right], numbers[left]];
    }
  }

  dfs(0);
  return permutationList
}


/**
 * O(n!), O(n!)
 * backtracking
 * @param {number[]} nums
 * @return {number[][]}
 */
var permute = function (nums) {
  const perList = [];

  function dfs(prefix, postfix) {
    if (prefix.length == nums.length) {
      perList.push([...prefix]); // Copy prefix to perList
      return
    }

    for (let index = 0; index < postfix.length; index++) {
      dfs([...prefix, postfix[index]],
        [...postfix.slice(0, index), ...postfix.slice(index + 1)]);
    }
  }

  dfs([], nums)
  return perList
}

/**
 * @param {character[][]} board
 * @param {string} word
 * @return {boolean}
 */
var exist = function (board, word) {
  let rows = board.length;
  let cols = board[0].length;
  snake = new Set();  // set of visited (forbidden) cells

  function dfs(row, col, index, snake) {
      if (index == word.length) return true  // if 'word' it empty all letters matched

      snake.add(`${row},${col}`);    // add cell pair to tabu set

      // check up, down, left, right for neighbouns
      if (col + 1 < cols &&  // check if out of bounds and
          !snake.has(`${row},${col + 1}`) &&  // if cell is not in tabo set
          board[row][col + 1] == word[index] &&  // Check if the current position matches the word's character
          dfs(row, col + 1, index + 1, snake)) {  // Switch to that letter and check its neighbors
          return true
      }

      if (col - 1 >= 0 &&
          !snake.has(`${row},${col - 1}`) &&
          board[row][col - 1] == word[index] &&
          dfs(row, col - 1, index + 1, snake)) {
          return true
      }

      if (row + 1 < rows &&
          !snake.has(`${row + 1},${col}`) &&
          board[row + 1][col] == word[index] &&
          dfs(row + 1, col, index + 1, snake)) {
          return true
      }

      if (row - 1 >= 0 &&
          !snake.has(`${row - 1},${col}`) &&
          board[row - 1][col] == word[index] &&
          dfs(row - 1, col, index + 1, snake)) {
          return true
      }

      snake.delete(`${row},${col}`);  // Backtrack: remove the current cell from tabu set
      
      //return false
  }


  for (let row = 0; row < rows; row++) {
      for (let col = 0; col < cols; col++) {
          if (board[row][col] === word[0]) {
              if (dfs(row, col, 1, new Set())) {
                  return true
              }
          }
      }
  }
  return false
}

