/**
 * Fibonnacci problem
 * dfs, unefficient
 * O(2^n), O(n)
 * counter as shared variable (list)
 * @param {number} n
 * @return {number}
 */
var climbStairs = function (n) {
  const counter = [];
  
  function dfs(index) {
    if (index > n) return

    if (index === n) {
      counter.push(1)
      return
    }

    dfs(index + 1);
    dfs(index + 2);
  }

  dfs(0)

  return counter.length
}


