class Solution:
    def minCostClimbingStairs(self, cost: list[int]) -> int:
        """
        O(n), O(1)
        dp, bottom-up
        """
        cache = [cost[0], cost[1]]

        for index in range(2, len(cost)):
            prev = cache[0]
            prev_prev = cache[1]
            cache = [cache[1], cost[index] + min(prev, prev_prev)]

        return min(cache[-1], cache[-2])


class Solution:
