    return 0
  }

  const stack = [];
  stack.push([root, 1])
  maxDepthValue = 1

  while (stack.length !== 0) {
    let [node, depth] = stack.pop();

    if (node.left) {
      stack.push([node.left, depth + 1]);
      maxDepthValue = Math.max(maxDepthValue, depth + 1);
    }

    if (node.right) {
      stack.push([node.right, depth + 1]);
      maxDepthValue = Math.max(maxDepthValue, depth + 1);
    }
  }

  return maxDepthValue
}


