      }

      if (!pNode.right && !qNode.right) {
        // pass
      } else if (
        pNode.right && qNode.right &&
        pNode.right.val === qNode.right.val) {
          pQueue.push(pNode.right);
          qQueue.push(qNode.right);
      } else {
        return false
      }
    }
  }
  return true
