class Solution:
    def containsNearbyDuplicate(self, numbers: list[int], window_length: int) -> bool:
        left = 0
        window = set()

        for right, number in enumerate(numbers):
            if number in window:
                return True
            else:
                window.add(number)

            if right - left + 1 == window_length + 1:
                window.remove(numbers[left])  # discard
                left += 1

        return False


