                nums1[index] = nums1[m]
                m -= 1
            else:
                nums1[index] = nums2[n]
                n -= 1
            
        # reaplacing nums1 with nums2 from the start
        if n != -1:
            for index in range(n + 1):
                nums1[index] = nums2[index]

