  m--;
  n--;

  for (let index = m + n + 1; index >= 0; index--) {
    if (n === -1) return nums1

    if (m === - 1) break

    if (nums1[m] > nums2[n]) {
      nums1[index] = nums1[m];
      m--;
    } else {
      nums1[index] = nums2[n];
      n--;
    }
  }

  for (let index = 0; index <= n; index++) {
    nums1[index] = nums2[index];
  }

  return nums1
}

