  let counter = 0;
  let subarraySum = 0;

  for (let index = 0; index < numbers.length - size + 1; index++) {
    if (index === 0) {
      subarraySum = numbers.slice(0, size).reduce((total, current) => total + current)
    } else {
      subarraySum += - numbers[index - 1] + numbers[index + size - 1]
    }

    if (subarraySum / size >= threshold) {
      counter++;
    }
  }
  
  return counter
