        for index1 in range(rowIndex - 1):
            new_row = [0] * (index1 + 2)  # new row
            new_row[0] = 1  # every row starts with `1`
            row = triangle[-1]  # previous row

            for index2 in range(index1):
                new_row[index2 + 1] = sum(row[index2: index2 + 2])
            
