  const triangle = [];

  for (let index = 0; index < numRows; index++) {
      if (index === 0) {
          triangle.push([1]);
      } else {
          const row = [1];

          for (let number = 0; number < index - 1; number++) {
              const first = triangle[triangle.length - 1][number];
              const second = triangle[triangle.length - 1][number + 1];
              row.push(first + second);
          }

          row.push(1);
          triangle.push(row);
      }
  }
  return triangle
}

