  let windowSum = 0;
  let left = 0;
  const target = numbers.reduce((total, current) => total + current) - x

  for (let right = 0; right < numbers.length; right++) {
    const number = numbers[right];
    windowSum += number;

    while (left <= right && windowSum > target) {
      windowSum -= numbers[left];
      left += 1;
