# recursion
class Solution:
    def decodeString(self, text: str) -> str:
        self.index = 0
        
        def recursion():
            number = 0
            word = ""
            
            while self.index < len(text):
                char = text[self.index]

                if char.isdigit():
                    number += 10 * number + int(char)
                    self.index += 1
                elif char == "[":
                    self.index += 1
                    word += number * recursion()
                    number = 0
                elif char == "]":
                    self.index += 1
                    return word
                else:
                    word += char
                    self.index += 1

            return word
            
        return recursion()


