class Trie {
  constructor() {
    this.root = new TrieNode();
  }
  /**
   * @param {string} word
   * @return {void}
   */
  insert(word) {
    let node = this.root;

    for (const letter of word) {
      if (!node.children.has(letter)) node.children.set(letter, new TrieNode());
      node = node.children.get(letter);
    }
    node.lastWord = true;
  }
  /**
   * @param {string} word
   * @return {boolean}
   */
  search(word) {
    let node = this.root;

    for (const letter of word) {
      if (!node.children.has(letter)) return false;
      node = node.children.get(letter);
    }
    return node.lastWord;
  }
  /**
   * @param {string} prefix
   * @return {boolean}
   */
  startsWith(prefix) {
    let node = this.root;

    for (const letter of prefix) {
      if (!node.children.has(letter)) return false;
      node = node.children.get(letter);
    }
    return true;
  }
}




