/**
 * O(nlogn), O(n)
 * two pointers
 * @param {number[]} numbers
 * @param {number} target
 * @return {number}
 */
var numSubseq = function (numbers, target) {
  numbers.sort((a, b) => a - b);
  let counter = 0;
  let right = numbers.length - 1;
  const mod = 10 ** 9 + 7;
  const powerOfTwo = [1];
  
  for (let index = 1; index < numbers.length; index++) {
    powerOfTwo[index] = powerOfTwo[index - 1] * 2 % mod
  }

  for (let left = 0; left < numbers.length; left++) {
    while (
      left <= right &&
      numbers[left] + numbers[right] > target
    ) right--;

    if (left <= right) {
      counter += powerOfTwo[right - left];
      counter %= mod;
    }
  }

  return Number(counter)
}

/**
 * O(n), O(1)
 * sliding window
 * @param {number[]} numbers
 * @param {number} k
 * @return {number}
 */
var numSubarrayProductLessThanK = function (numbers, k) {
  let left = 0;
  let window = 1;
  let counter = 0;

  for (let right = 0; right < numbers.length; right++) {
    window *= numbers[right];

    while (
      left <= right &&
      window >= k
    ) {
      window /= numbers[left];
      left++;
    }

    counter += right - left + 1;
  }

  return counter
}

