class Solution:
    def removeDuplicates(self, letters: str, k: int) -> str:
        stack = []

        for letter in letters:
            is_substring = False

            if (stack and 
                    len(stack) >= k - 1):
                is_substring = True
                
                for index in range(1, k):
                    if stack[-index] != letter:
                        is_substring = False
                        break
                
                if is_substring:
                    for _ in range(k - 1):
                        stack.pop()
            
            if not is_substring:
                stack.append(letter)
        
        return "".join(stack)

/**
 * O(n), O(n)
 * stack
 * @param {string} letters
 * @param {number} k
 * @return {string}
 */
var removeDuplicates = function (letters, k) {
  const stack = [];  // [[letter, frequency]]

  for (const letter of letters) {
    if (
      stack.length > 0 &&
      stack[stack.length - 1][0] === letter
    ) {
      stack[stack.length - 1][1] += 1;

      if (stack[stack.length - 1][1] === k)
        stack.pop();
    }
    else { stack.push([letter, 1]); }

  }

  return (stack
    .map(([letter, frequency]) => letter.repeat(frequency))
    .join(''))
}

