# binary search
class Solution:
    def successfulPairs(self, spells: list[int], potions: list[int], success: int) -> list[int]:
        potions.sort()
        success_list = [0] * len(spells)

        for index, spell in enumerate(spells):
            left = 0
            right = len(potions) - 1

            while left < right:
                middle = left + (right - left) // 2
                middle_potion = potions[middle]

                if middle_potion * spell < success:
                    left = middle + 1
                else:
                    right = middle

            if potions[right] * spell >= success:
                success_list[index] = len(potions) - right
        
        return success_list

/**
 * O(nlogn), O(n)
 * binary search
 * @param {number[]} spells
 * @param {number[]} potions
 * @param {number} success
 * @return {number[]}
 */
var successfulPairs = function (spells, potions, success) {
  potions.sort((a, b) => a - b);
  const successList = Array(spells.length).fill(0);

  for (let index = 0; index < spells.length; index++) {
    const spell = spells[index];
    let left = 0;
    let right = potions.length - 1;

    while (left < right) {
      const middle = left + (right - left) / 2 | 0;

      if (potions[middle] * spell < success) 
        left = middle + 1;
      else
        right = middle;
    }

    if (potions[right] * spell >= success)
      successList[index] = potions.length - right;
  }
  
  return successList
}

/**
 * Definition for singly-linked list.
 * function ListNode(val, next) {
 *     this.val = (val===undefined ? 0 : val)
 *     this.next = (next===undefined ? null : next)
 * }
 */
/**
 * O(n), O(1)
 * linked list, one pass
 * @param {ListNode} head
 * @param {number} k
 * @return {ListNode}
 */
var swapNodes = function (head, k) {
  let node = head;
  let left = head;
  let right = head;

  for (let index = 0; index < k - 1; index++) {
    left = left.next;
    node = node.next;
  }

  while (node.next) {
    node = node.next;
    right = right.next;
  }

  [left.val, right.val] = [right.val, left.val];

  return head
}

