    def is_word_good(self, word: str, counter: dict) -> bool:
        counter_copy = counter.copy()

        for letter in word:
            if letter not in counter_copy:
                return
            else:
                if counter_copy[letter] == 0:
                    return
                counter_copy[letter] -= 1
        
        return True

