var restoreIpAddresses = function (text) {
  if (
    text.length < 4 ||
    text.length > 12) return []

  const ip = [];
  const ipList = [];

  function dfs(index) {
    if (index === text.length) {
      if (ip.length === 4) {
        ipList.push(ip
          .map((x) => x[0])
          .join('.')
        );
      }
      return
    }

    // check for 0:9
    ip.push([text[index]]);
    dfs(index + 1);
    ip.pop();

    // check for 10:99
    if (
      index < text.length - 1 &&
      text[index] != '0'
    ) {
      ip.push([text.slice(index, index + 2)]);
      dfs(index + 2);
      ip.pop();
    }

    // check for 100:255
    if (
      index < text.length - 2 &&
      text[index] != '0' &&
      text.slice(index, index + 3) <= '255'
    ) {
      ip.push([text.slice(index, index + 3)]);
      dfs(index + 3);
      ip.pop();
    }
  }

  dfs(0);
  return ipList
}

