function insertionSort(numbers) {
  for (let right = 1; right < numbers.length; right++) {
    const number = numbers[right];
    let left = right - 1;

    while (
      left >= 0 &&
      numbers[left] > number
    ) {
      numbers[left + 1] = numbers[left];
      left--;
    }

    numbers[left + 1] = number;
  }
