class Solution:
    def sortColors(self, nums: list[int]) -> None:
        left = 0
        right = len(nums) - 1
        index = 0

        while index <= right:
            number = nums[index]

            if number == 0:
                nums[left], nums[index] = nums[index], nums[left]
                left += 1
            elif number == 2:
                nums[index], nums[right] = nums[right], nums[index]
                right -= 1
                # swapped value should be checked at the next iteration
                index -= 1

            index += 1

        return nums

