 * sliding window
 * @param {number[]} numbers
 * @return {boolean}
 */
var find132pattern = function (numbers) {
  const stack = [];
  let minLeft = numbers[0];

  for (const number of numbers) {
    while (stack.length > 0 &&
      number >= stack[stack.length - 1][0]
    ) stack.pop()

    if (
      stack.length > 0 &&
      number < stack[stack.length - 1][0] &&
      number > stack[stack.length - 1][1]
