# backtracking
class Solution:
    def makesquare(self, matchsticks: list[int]) -> bool:
        if sum(matchsticks) % 4:
            return False

        matchsticks.sort(reverse=True)
        max_side_len = sum(matchsticks) // 4
        side_len = [0] * 4  # each side length cache

        def dfs(index):
            if index == len(matchsticks):
                return True

            for side in range(4):  # check every square side
                if side_len[side] + matchsticks[index] <= max_side_len:  # if side length in bounds
                    side_len[side] += matchsticks[index]
                    
                    if dfs(index + 1):  # check next match
                        return True
                    
                    side_len[side] -= matchsticks[index]  # backtrack
            
            return False

        return dfs(0)

/**
 * O(4^n), O(n)
 * backtracking
 * @param {number[]} matchsticks
 * @return {boolean}
 */
var makesquare = function (matchsticks) {
  if (matchsticks.reduce((total, current) => total + current) % 4)
    return false

  matchsticks.sort((a, b) => b - a);
  const sideLength = matchsticks.reduce((total, current) => total + current) / 4;
  const currentSideLength = new Array(4).fill(0);

  function dfs(index) {
    if (index === matchsticks.length) return true

    for (let side = 0; side < 4; side++) {
      if (currentSideLength[side] + matchsticks[index] <= sideLength) {
        currentSideLength[side] += matchsticks[index]
        if (dfs(index + 1)) return true
        currentSideLength[side] -= matchsticks[index]
      }
    }
    return false
  }

  return dfs(0)
}

/**
 * O(n3), O(n)
 * dp, bottom-up, optimal
 * @param {string} text
 * @param {string[]} dictionary
 * @return {number}
 */
var minExtraChar = function (text, dictionary) {
  const cache = Array(text.length + 1).fill(0);
  const words = new Set(dictionary);
  const wordLengths = new Set(dictionary.map(word => word.length))

  for (let index = text.length - 1; index >= 0; index--) {
    cache[index] = cache[index + 1] + 1;

    for (const wordLength of wordLengths) {
      if (
        index + wordLength <= text.length &&
        words.has(text.slice(index, index + wordLength))
      ) {
        cache[index] = Math.min(cache[index], cache[index + wordLength]);
      }
    }
  }

  return cache[0]
}


class TrieNode {
  constructor() {
    this.letters = new Map();
    this.isWord = false;
  }
}

class Trie {
  constructor() {
    this.root = new TrieNode()
  }

  insert(word) {
    let node = this.root;

    for (const letter of word) {
      if (!node.letters.has(letter))
        node.letters.set(letter, new TrieNode());

      node = node.letters.get(letter);
    }
    node.isWord = true;
  }

  search(word) {
    let node = this.root;

    for (const letter of word) {
      if (!node.letters.has(letter))
        return false

      node = node.letters.get(letter);
    }
    return node.isWord
  }
}

/**
 * O(n3), O(n2)
 * dp, bottom-up, trie
 * @param {string} text
 * @param {string[]} dictionary
 * @return {number}
 */
var minExtraChar = function (text, dictionary) {
  const trie = new Trie();
  for (const word of dictionary) {
    trie.insert(word);
  }

  const cache = Array(text.length + 1).fill(0);
  const words = new Set(dictionary);
  const wordLengths = new Set(dictionary.map(word => word.length))

  for (let index = text.length - 1; index >= 0; index--) {
    cache[index] = cache[index + 1] + 1;

    for (const wordLength of wordLengths) {
      if (
        index + wordLength <= text.length &&
        trie.search(text.slice(index, index + wordLength))
      ) {
        cache[index] = Math.min(cache[index], cache[index + wordLength]);
      }
    }
  }

  return cache[0]
}

