 *     this.right = (right===undefined ? null : right)
 * }
 */
/**
 * @param {TreeNode} root
 * @return {string}
 */
var tree2str = function (root) {
  const text = [];

  var dfs = function (node) {
    if (!node) return

    text.push(node.val);

    if (node.left || node.right)
      text.push('(');
    dfs(node.left);
    if (node.left || node.right)
      text.push(')');

    if (node.right)
      text.push('(');
    dfs(node.right);
    if (node.right)
      text.push(')');
  }

  dfs(root);
  return text.join('')
}

