# O(n), O(1)
# binary search, iteration
class Solution:
    def insertIntoBST(self, root: TreeNode, val: int) -> TreeNode:
        if not root:
            return TreeNode(val)
        
        queue = deque([root])

        while queue:
            node = queue.popleft()

            if val < node.val:
                if not node.left:
                    node.left = TreeNode(val)
                else:
                    queue.append(node.left)
            else:
                if not node.right:
                    node.right = TreeNode(val)
