 */
var minOperations = function(numbers) {
  const numberSet = new Set(numbers);
  let difference;
  minDifference = numbers.length;

  for (const number of numberSet) {
    difference = 0;

    for (let index = 1; index < numbers.length; index++)
    {
      difference += !numberSet.has(number + index);
    }

    minDifference = Math.min(minDifference, difference);
  }

  return minDifference
}

/**
 * O(1), O(1)
 * Design, Array, String, Iterator
 * generator
 */

class StringIterator {
  constructor(compressedString) {
    this.generator = this.generate(compressedString);
    this.letter = this._getNextLetter();
  }

  *generate(compressedString) {
    let index = 0;

    while (index < compressedString.length) {
      let letter = compressedString[index];
      index++;
      let frequency = 0;

      while (
        index < compressedString.length &&
        /\d/.test(compressedString[index])
      ) {
        frequency = 10 * frequency + Number(compressedString[index]);
        index++;
      }

      for (let f = 0; f < frequency; f++) {
        yield letter;
      }
    }
  }

  _getNextLetter() {
    const { value, done } = this.generator.next();
    return done ? ' ' : value;
  }

  next() {
    const currentLetter = this.letter;
    this.letter = this._getNextLetter();
    return currentLetter;
  }

  hasNext() {
    return this.letter !== ' ';
  }
}


/**
 * O(n): constructor; O(1): next, hasNext; aux space O(n)
 * Design, Array, String, Iterator
 */
class StringIterator {
  constructor(text) {
    this.index = 0;
    this.text = [];
    this.strToList(text);
  };

  /**
   * @param {string} text
   * @return {}
   */
  strToList(text) {
    let frequency = 0;
    for (const char of text) {
      if (char.match(/[A-Za-z]/)) {
        if (frequency) {
          this.text.push(frequency);
          frequency = 0;
        }
        this.text.push(char);
      } else if (char.match(/\d/)) {
        frequency = 10 * frequency + Number(char);
      }
    }
    this.text.push(frequency)
  };

  /**
   * @param {}
   * @return {string}
   */
  next() {
    if (!this.hasNext())
      return ' '

    const letter = this.text[this.index];
    this.text[this.index + 1] -= 1;
    if (!this.text[this.index + 1])
      this.index += 2;
    return letter
  };

  /**
   * @param {}
   * @return {boolean}
   */
  hasNext() {
    return this.index < this.text.length
  };
}


