# O(logn), O(n)
# heap
class FreqStack:
    def __init__(self):
        self.stack = []  # [((-frequency, -index), value), ]
        self.counter = {}
        self.index = 0

    def push(self, val: int) -> None:
        self.counter[val] = self.counter.get(val, 0) + 1
        heapq.heappush(self.stack, ((-self.counter[val], -self.index), val))
        self.index += 1

    def pop(self) -> int:
        val = heapq.heappop(self.stack)[1]
        self.counter[val] -= 1
        return val


