/**
 * O(n), O(n)
 * dp, bottom-up with tabulation as hash map
 * @param {number[][]} questions
 * @return {number}
 */
var mostPoints = function (questions) {
  const cache = new Map();

  for (let index = questions.length - 1; index > -1; index--) {
    cache.set(index,
      Math.max(
        questions[index][0] + (cache.get(index + 1 + questions[index][1]) ?? 0),
        cache.get(index + 1) ?? 0
      ));
  }

  return cache.get(0)
};

/**
 * O(n), O(n)
 * dp, bottom-up
 * @param {number} low
 * @param {number} high
 * @param {number} zero
 * @param {number} one
 * @return {number}
 */
var countGoodStrings = function (low, high, zero, one) {
  const mod = 10 ** 9 + 7;
  const cache = new Map([[0, 1]]);

  for (let index = 1; index <= high; index++) {
    const value = (
      (cache.get(index - zero) ?? 0) + 
      (cache.get(index - one) ?? 0)) % mod;
    cache.set(index, value);
  }

  let counter = 0;
  for (let index = low; index <= high; index++) {
    counter += cache.get(index);
  }

  return counter % mod
};

import java.util.HashSet;
import java.util.Set;

/**
 * This class provides a solution to check if an array contains duplicate
 * elements.
 */
public class Solution {
  /**
   * Checks if the given array contains any duplicate elements.
   *
   * @param numbers The array of integers to check for duplicates.
   * @return {@code true} if the array contains duplicates, {@code false}
   *         otherwise.
   */
  public boolean containsDuplicate(int[] numbers) {
    Set<Integer> uniqueNumbers = new HashSet<>();
    for (int number : numbers) {
      if (uniqueNumbers.contains(number)) {
        return true;
      } else {
        uniqueNumbers.add(number);
      }
    }
    return false;
  }

  public static void main(String[] agrs) {
    Solution solution = new Solution();
    int[] numbers = { 1, 2, 3, 1 };
    boolean result = solution.containsDuplicate(numbers);
    System.out.println(result);
  }
}

class Solution {
public:
    bool containsDuplicate(vector<int>& numbers) {
        set<int> uniqueNumbers;
        for (int number : numbers) {
            if (uniqueNumbers.contains(number)) {
                return true;
            } else {
                uniqueNumbers.insert(number);
            }
        }
        return false;
    }
};
